mapper.select:
SELECT no, id, name, msg, star, vno, TO_CHAR(regdate, 'YYYY-MM-DD HH24:MI:SS') as dbday FROM junggo_review 
WHERE vno=#{vno}
ORDER BY no DESC

mapper.update:
UPDATE junggo_review SET
msg=#{msg}
WHERE no=#{no}

mapper.select:
select NVL(round(avg(star), 1), 0.0) as avg
from junggo_review
where vno=#{vno}
